value.script:
{  

  // Calculate suggested CPUs depending on the input BAM size
  bam_size = $job.inputs.input_bam.size

  if(!bam_size) { bam_size = 0 }

  GB_1 = 1024*1024*1024

  if(bam_size < GB_1)
  { suggested_cpus = 1 }
  else if(bam_size < 10 * GB_1)
  { suggested_cpus = 8 }
  else 
  { suggested_cpus = 30 }

  if($job.inputs.threads){  	
    return  $job.inputs.threads
  } 
  else{  	
    return suggested_cpus  
  }

}

{  

  // Calculate suggested memory depending on the input BAM size
  bam_size = $job.inputs.input_bam.size

  if(!bam_size) { bam_size = 0 }

  GB_1 = 1024*1024*1024
  if(bam_size < GB_1){ suggested_memory = 4 }
  else if(bam_size < 10 * GB_1){ suggested_memory = 15 }
  else { suggested_memory = 58 }

  if($job.inputs.memory){  	
    return  $job.inputs.memory* 1024  
  } 
  else{  	
    return suggested_memory * 1024  
  }
}

